import os
import numpy as np

def extract_labels_from_filename(filename):
    """Extrai as labels de direção e status a partir do nome do arquivo."""
    parts = filename.split('_')
    maneuvering_direction = parts[1]  # Extraímos a direção de manobra
    fault = parts[2]  # Extraímos o status (falha)

    # Mapeamento das direções para valores numéricos
    direction_map = {'F': 0, 'B': 1, 'R': 2, 'L': 3, 'C': 4, 'CC': 5}
    direction_label = direction_map[maneuvering_direction]

    # Mapeamento dos status para valores numéricos
    fault_map = {'N': 0, 'MF1': 1, 'MF2': 2, 'MF3': 3, 'MF4': 4,
                 'PC1': 5, 'PC2': 6, 'PC3': 7, 'PC4': 8}
    status_label = fault_map[fault]

    return direction_label, status_label

def generate_labels_for_dataset(directory_path):
    """Gera as labels de direção e status para todos os arquivos de um diretório."""
    direction_labels = []
    status_labels = []

    for filename in os.listdir(directory_path):
        if filename.endswith('.wav'):
            direction_label, status_label = extract_labels_from_filename(filename)
            direction_labels.append(direction_label)
            status_labels.append(status_label)

    return np.array(direction_labels), np.array(status_labels)

def generate_labels_for_drone(drone_type, base_dir='/content'):
    """Gera labels para treino, validação e teste de um tipo de drone específico.

    Args:
        drone_type (str): O tipo de drone (A, B ou C).
        base_dir (str): O diretório base onde os dados dos drones estão localizados.

    Returns:
        dict: Um dicionário contendo as labels de direção e status para treino, validação e teste.
    """
    train_dir_mic1 = os.path.join(base_dir, drone_type, 'train/mic1')
    train_dir_mic2 = os.path.join(base_dir, drone_type, 'train/mic2')
    valid_dir_mic1 = os.path.join(base_dir, drone_type, 'valid/mic1')
    valid_dir_mic2 = os.path.join(base_dir, drone_type, 'valid/mic2')
    test_dir_mic1 = os.path.join(base_dir, drone_type, 'test/mic1')
    test_dir_mic2 = os.path.join(base_dir, drone_type, 'test/mic2')

    # Gerar labels para Mic1 (Assumindo que as labels são as mesmas para Mic2)
    y_train_direction_mic1, y_train_status_mic1 = generate_labels_for_dataset(train_dir_mic1)
    y_val_direction_mic1, y_val_status_mic1 = generate_labels_for_dataset(valid_dir_mic1)
    y_test_direction_mic1, y_test_status_mic1 = generate_labels_for_dataset(test_dir_mic1)

    # Mic1 e Mic2 possuem as mesmas labels
    y_train_direction = y_train_direction_mic1
    y_train_status = y_train_status_mic1
    y_val_direction = y_val_direction_mic1
    y_val_status = y_val_status_mic1
    y_test_direction = y_test_direction_mic1
    y_test_status = y_test_status_mic1

    return {
        'y_train_direction': y_train_direction,
        'y_train_status': y_train_status,
        'y_val_direction': y_val_direction,
        'y_val_status': y_val_status,
        'y_test_direction': y_test_direction,
        'y_test_status': y_test_status
    }

