import os
import numpy as np
from collections import Counter
import matplotlib.pyplot as plt
from tabulate import tabulate

# Função para extrair o background do nome do arquivo
def extrair_background(filename, is_train=False):
    parts = filename.split('_')
    if is_train:
        background = parts[5]  # O background está na 6ª posição (index 5) para os arquivos de treino
    else:
        background = parts[4]  # O background está na 5ª posição (index 4) para os arquivos de teste e validação
    return background

# Função para contar os tipos de background em um diretório
def contar_backgrounds(dir_mic, is_train=False):
    backgrounds = []
    for root, dirs, files in os.walk(dir_mic):
        for file in files:
            if file.endswith('.wav'):
                background = extrair_background(file, is_train)
                backgrounds.append(background)
    return Counter(backgrounds)

# Função para plotar a distribuição de backgrounds entre mic1 e mic2
def plot_background_distribution(counter_mic1, counter_mic2, title):
    # Combinar as chaves de ambos os contadores
    background_classes = sorted(set(counter_mic1.keys()).union(set(counter_mic2.keys())))

    total_counts_mic1 = sum(counter_mic1.values())
    total_counts_mic2 = sum(counter_mic2.values())

    values_mic1 = [counter_mic1.get(cls, 0) for cls in background_classes]
    values_mic2 = [counter_mic2.get(cls, 0) for cls in background_classes]

    bar_width = 0.35
    r1 = np.arange(len(background_classes))
    r2 = [x + bar_width for x in r1]

    plt.figure(figsize=(14, 7))
    bars1 = plt.bar(r1, values_mic1, color='blue', width=bar_width, edgecolor='grey', label='Mic1')
    bars2 = plt.bar(r2, values_mic2, color='orange', width=bar_width, edgecolor='grey', label='Mic2')

    plt.xlabel('Tipos de Background', fontweight='bold')
    plt.ylabel('Número de Amostras', fontweight='bold')
    plt.title(title)
    plt.xticks([r + bar_width/2 for r in range(len(background_classes))], background_classes, rotation=45)
    plt.legend()

    # Adicionar quantidades e porcentagens em cima de cada barra
    for bar, count in zip(bars1, values_mic1):
        percentage = 100 * count / total_counts_mic1
        plt.text(bar.get_x() + bar.get_width() / 2, bar.get_height(), f'{count} ({percentage:.1f}%)',
                 ha='center', va='bottom', fontsize=10, fontweight='bold')

    for bar, count in zip(bars2, values_mic2):
        percentage = 100 * count / total_counts_mic2
        plt.text(bar.get_x() + bar.get_width() / 2, bar.get_height() - 10, f'{count} ({percentage:.1f}%)',
                 ha='center', va='top', fontsize=10, fontweight='bold')

    plt.tight_layout()
    plt.show()

# Função para imprimir a tabela de contagens e o total usando tabulate
def print_background_table(counter_mic1, counter_mic2, title):
    total_counts_mic1 = sum(counter_mic1.values())
    total_counts_mic2 = sum(counter_mic2.values())

    combined_counter = counter_mic1 + counter_mic2
    total_counts_combined = sum(combined_counter.values())

