def save_combined_audio_with_original_names(X_mic1, X_mic2, filenames_mic1, sr, save_dir, dataset_type, drone_type):
    # Cria os diretórios para salvar os arquivos se eles não existirem
    os.makedirs(os.path.join(save_dir, drone_type, dataset_type), exist_ok=True)

    # Salva cada arquivo combinado de mic1 e mic2 como estéreo, preservando os nomes originais
    for i, (audio_mic1, audio_mic2, filename_mic1) in enumerate(zip(X_mic1, X_mic2, filenames_mic1)):
        # Modifica o nome do arquivo para indicar que é a combinação de MIC1 e MIC2
        filename_mic12 = filename_mic1.replace('mic1', 'mic12')
        save_path = os.path.join(save_dir, drone_type, dataset_type, filename_mic12)

        # Verifica se o arquivo já existe
        if not os.path.exists(save_path):
            combined_audio = np.stack([audio_mic1, audio_mic2], axis=-1)  # Combina mic1 e mic2 em estéreo
            sf.write(save_path, combined_audio, sr)
            print(f"Arquivo salvo: {save_path}")
        else:
            print(f"Arquivo já existe: {save_path}, pulando...")

def load_all_audio_from_directory_with_filenames(directory):
    signals = []
    filenames = []
    sample_rate = None
    for root, _, files in os.walk(directory):
        for file in sorted(files):  # Ordena para garantir que os arquivos correspondentes em mic1 e mic2 sejam combinados corretamente
            if file.endswith('.wav'):
                filepath = os.path.join(root, file)
                signal, sr = librosa.load(filepath, sr=None)
                signals.append(signal)
                filenames.append(file)
                if sample_rate is None:
                    sample_rate = sr
    return np.array(signals), filenames, sample_rate

