"""# Definindo funções para extração de características do conjunto de dados dos drones A, B e C:

47 MIN
"""

import os
import numpy as np
import librosa
from scipy.fftpack import dct
from gammatone.gtgram import gtgram

def extract_mfcc(signal, sr, n_mfcc=13, n_fft=2048, hop_length=512, n_mels=16):
    stft = np.abs(librosa.stft(signal, n_fft=n_fft, hop_length=hop_length))
    power_spectrum = np.square(stft)
    mel_filter_bank = librosa.filters.mel(sr=sr, n_fft=n_fft, n_mels=n_mels)
    mel_spectrum = np.dot(mel_filter_bank, power_spectrum)
    log_mel_spectrum = np.log(mel_spectrum + 1e-9)
    mfcc = dct(log_mel_spectrum, type=2, axis=0, norm='ortho')[:n_mfcc]
    return mfcc

def extract_gtcc(signal, sr, n_gtcc=13, n_fft=2048, hop_length=512, n_gammas=16, f_min=50):
    # Parâmetros do Gammatone
    window_size = n_fft / sr  # Tamanho da janela em segundos
    hop_size = hop_length / sr  # Tamanho do salto em segundos

    # Corrigir valores de hop_size para evitar divisão por zero
    if hop_size <= 0:
        hop_size = 1 / sr  # Definir um valor mínimo

    # Extrair o espectrograma Gammatone
    gamma_spectrum = gtgram(signal, sr, window_size, hop_size, n_gammas, f_min)

    # Log energia
    log_gamma_spectrum = np.log(gamma_spectrum + 1e-9)

    # Aplicar a DCT para obter os coeficientes GTCC
    gtcc = dct(log_gamma_spectrum, type=2, axis=0, norm='ortho')[:n_gtcc]

    return gtcc

def extract_mfcc_gtcc(signals, sr):
    mfcc_list = []
    gtcc_list = []
    for signal in signals:
        mfcc = extract_mfcc(signal, sr)
        gtcc = extract_gtcc(signal, sr)
        mfcc_list.append(mfcc)
        gtcc_list.append(gtcc)
    return np.array(mfcc_list), np.array(gtcc_list)

def extract_features_for_drone(drone_type, base_dir='/content'):
    """Extrai as características MFCC e GTCC para os conjuntos de treino, validação e teste de um tipo de drone específico.

    Args:
        drone_type (str): O tipo de drone (A, B ou C).
        base_dir (str): O diretório base onde os dados dos drones estão localizados.

    Returns:
        dict: Um dicionário contendo as características MFCC e GTCC para treino, validação e teste.
    """
    train_dir_mic1 = os.path.join(base_dir, drone_type, 'train/mic1')
    train_dir_mic2 = os.path.join(base_dir, drone_type, 'train/mic2')
    valid_dir_mic1 = os.path.join(base_dir, drone_type, 'valid/mic1')
    valid_dir_mic2 = os.path.join(base_dir, drone_type, 'valid/mic2')
    test_dir_mic1 = os.path.join(base_dir, drone_type, 'test/mic1')
    test_dir_mic2 = os.path.join(base_dir, drone_type, 'test/mic2')

    # Carregar sinais de áudio para Mic1 e Mic2
    X_train_mic1, sr_train_mic1 = load_all_audio_from_directory(train_dir_mic1)
    X_train_mic2, sr_train_mic2 = load_all_audio_from_directory(train_dir_mic2)
    X_val_mic1, sr_val_mic1 = load_all_audio_from_directory(valid_dir_mic1)
    X_val_mic2, sr_val_mic2 = load_all_audio_from_directory(valid_dir_mic2)
    X_test_mic1, sr_test_mic1 = load_all_audio_from_directory(test_dir_mic1)
    X_test_mic2, sr_test_mic2 = load_all_audio_from_directory(test_dir_mic2)

    # Extrair características MFCC e GTCC
    mfcc_train, gtcc_train = extract_mfcc_gtcc(X_train_mic1, sr_train_mic1)
    mfcc_val, gtcc_val = extract_mfcc_gtcc(X_val_mic1, sr_val_mic1)
    mfcc_test, gtcc_test = extract_mfcc_gtcc(X_test_mic1, sr_test_mic1)

    return {
        'mfcc_train': mfcc_train,
        'gtcc_train': gtcc_train,
        'mfcc_val': mfcc_val,
        'gtcc_val': gtcc_val,
        'mfcc_test': mfcc_test,
        'gtcc_test': gtcc_test
    }

