from sklearn.preprocessing import StandardScaler

def normalize_features(features_dict, scaler_dict=None):
    """
    Normaliza as características MFCC e GTCC para os drones A, B e C.

    Args:
        features_dict (dict): Um dicionário contendo as características MFCC e GTCC para os drones A, B e C.
        scaler_dict (dict): Um dicionário contendo os scalers para MFCC e GTCC de cada drone. Se None, serão criados novos scalers.

    Returns:
        dict: Um dicionário com as características normalizadas e os scalers utilizados.
    """
    normalized_features = {}
    scalers = {}

    for drone, features in features_dict.items():
        normalized_features[drone] = {}
        scalers[drone] = {}

        if scaler_dict is None:
            scalers[drone]['mfcc_scaler'] = StandardScaler()
            scalers[drone]['gtcc_scaler'] = StandardScaler()
        else:
            scalers[drone]['mfcc_scaler'] = scaler_dict[drone].get('mfcc_scaler', StandardScaler())
            scalers[drone]['gtcc_scaler'] = scaler_dict[drone].get('gtcc_scaler', StandardScaler())

        # Normalizar MFCC
        mfcc_features = features['mfcc']
        scaled_mfcc = scalers[drone]['mfcc_scaler'].fit_transform(mfcc_features.reshape(-1, mfcc_features.shape[-1])).reshape(mfcc_features.shape)
        normalized_features[drone]['mfcc'] = scaled_mfcc

        # Normalizar GTCC
        gtcc_features = features['gtcc']
        scaled_gtcc = scalers[drone]['gtcc_scaler'].fit_transform(gtcc_features.reshape(-1, gtcc_features.shape[-1])).reshape(gtcc_features.shape)
        normalized_features[drone]['gtcc'] = scaled_gtcc

    return normalized_features, scalers

