# Função para extrair a SNR dos nomes dos arquivos
def extract_snr_from_filename(filename):
    """Extrai a SNR a partir do nome do arquivo."""
    snr_value = float(filename.split('_')[-1].split('=')[-1].replace('.wav', ''))
    return snr_value

# Função para extrair a SNR de todos os arquivos de um diretório
def extract_snr_from_directory(directory_path):
    """Extrai os valores de SNR de todos os arquivos de um diretório."""
    snr_values = []
    for filename in os.listdir(directory_path):
        if filename.endswith('.wav'):
            snr_value = extract_snr_from_filename(filename)
            snr_values.append(snr_value)
    return np.array(snr_values)

