from sklearn.model_selection import KFold
from tensorflow.keras.callbacks import EarlyStopping

# Parâmetros
num_folds = 5
epochs = 100
batch_size = 32

# Preparar K-Fold Cross-Validation
kf = KFold(n_splits=num_folds, shuffle=True, random_state=42)

# Early stopping
early_stopping = EarlyStopping(monitor='val_loss', patience=10, restore_best_weights=True)

# Dicionário para armazenar as métricas de cada fold
metrics = {
    'fold': [],
    'train_loss': [],
    'val_loss': [],
    'train_accuracy_status': [],
    'val_accuracy_status': [],
    'train_accuracy_direction': [],
    'val_accuracy_direction': []
}

# Loop pelos folds
fold_number = 1
for train_index, val_index in kf.split(X_train_combined_A):
    X_train_fold, X_val_fold = X_train_combined_A[train_index], X_train_combined_A[val_index]
    y_train_fold_status, y_val_fold_status = y_train_status_A[train_index], y_train_status_A[val_index]
    y_train_fold_direction, y_val_fold_direction = y_train_direction_A[train_index], y_train_direction_A[val_index]

    # Compilar o modelo
    model_A = build_deep_1dcnn_resnet(input_shape_A, num_classes_status, num_classes_direction, num_blocks=8)
    model_A.compile(optimizer=Adam(learning_rate=0.0001),
                    loss={'status_output': 'categorical_crossentropy', 'direction_output': 'categorical_crossentropy'},
                    metrics={'status_output': 'accuracy', 'direction_output': 'accuracy'})

    # Treinamento
    history = model_A.fit(X_train_fold, [y_train_fold_status, y_train_fold_direction],
                          validation_data=(X_val_fold, [y_val_fold_status, y_val_fold_direction]),
                          epochs=epochs,
                          batch_size=batch_size,
                          callbacks=[early_stopping])

    # Armazenar as métricas do último epoch de cada fold
    metrics['fold'].append(fold_number)
    metrics['train_loss'].append(history.history['loss'][-1])
    metrics['val_loss'].append(history.history['val_loss'][-1])
    metrics['train_accuracy_status'].append(history.history['status_output_accuracy'][-1])
    metrics['val_accuracy_status'].append(history.history['val_status_output_accuracy'][-1])
    metrics['train_accuracy_direction'].append(history.history['direction_output_accuracy'][-1])
    metrics['val_accuracy_direction'].append(history.history['val_direction_output_accuracy'][-1])

    fold_number += 1

