# Função para avaliar o modelo e gerar os gráficos
def evaluate_and_plot(model, X_test_combined, y_test_status, y_test_direction, drone_label):
    # Avaliação no conjunto de teste
    results = model.evaluate(X_test_combined, {'status_output': y_test_status, 'direction_output': y_test_direction})
    print(f"Drone {drone_label} - Test loss, Test accuracy:", results)

    # Fazer as previsões no conjunto de teste
    y_pred_status, y_pred_direction = model.predict(X_test_combined)

    # Converter as previsões de probabilidades para labels
    y_pred_status = np.argmax(y_pred_status, axis=1)
    y_pred_direction = np.argmax(y_pred_direction, axis=1)

    # Converter os labels de teste de one-hot para índices
    y_true_status = np.argmax(y_test_status, axis=1)
    y_true_direction = np.argmax(y_test_direction, axis=1)

    # Calcular o Macro F1 Score para o status
    f1_macro_status = f1_score(y_true_status, y_pred_status, average='macro')

    # Calcular o Macro F1 Score para a direção
    f1_macro_direction = f1_score(y_true_direction, y_pred_direction, average='macro')

    print(f"Drone {drone_label} - Macro F1 Score para Status: {f1_macro_status}")
    print(f"Drone {drone_label} - Macro F1 Score para Direção: {f1_macro_direction}")

    # Gerar a matriz de confusão para o status
    conf_matrix_status = confusion_matrix(y_true_status, y_pred_status)

    # Plotar a matriz de confusão para o status
    plt.figure(figsize=(10, 8))
    sns.heatmap(conf_matrix_status, annot=True, fmt='d', cmap='Blues', cbar=False)
    plt.title(f'Confusion Matrix - Status Classification (Drone {drone_label})')
    plt.xlabel('Predicted Label')
    plt.ylabel('True Label')
    plt.show()

    # Gerar a matriz de confusão para a direção
    conf_matrix_direction = confusion_matrix(y_true_direction, y_pred_direction)

