from tensorflow.keras.layers import Input, Conv1D, BatchNormalization, Activation, Add, Flatten, Dense, Dropout
from tensorflow.keras.models import Model
from tensorflow.keras.optimizers import Adam

# Função para criar e compilar o modelo ResNet com a identificação do drone
def build_and_compile_model(input_shape, num_classes_status, num_classes_direction, num_classes_drone, num_blocks=8):
    # Função para criar o bloco residual
    def residual_block(x, filters, kernel_size=3):
        res = x
        out = Conv1D(filters, kernel_size, padding='same')(x)
        out = BatchNormalization()(out)
        out = Activation('relu')(out)

        out = Conv1D(filters, kernel_size, padding='same')(out)
        out = BatchNormalization()(out)

        out = Add()([res, out])
        out = Activation('relu')(out)
        return out

    # Função para construir o modelo 1D CNN-ResNet
    def build_deep_1dcnn_resnet(input_shape, num_classes_status, num_classes_direction, num_classes_drone, num_blocks=8):
        inputs = Input(shape=input_shape)

        # Primeira camada Conv1D
        x = Conv1D(128, kernel_size=3, padding='same')(inputs)
        x = BatchNormalization()(x)
        x = Activation('relu')(x)

        # Blocos residuais
        for _ in range(num_blocks):
            x = residual_block(x, 128)

        x = Flatten()(x)

        # Camada densa antes das saídas
        x = Dense(256, activation='relu')(x)
        x = BatchNormalization()(x)
        x = Dropout(0.5)(x)  # Dropout para regularização

        # Cabeça de classificação de status (tarefa principal)
        status_output = Dense(num_classes_status, activation='softmax', name='status_output')(x)

        # Cabeça de classificação de direção (tarefa auxiliar)
        direction_output = Dense(num_classes_direction, activation='softmax', name='direction_output')(x)

        # Cabeça de identificação do drone (nova tarefa)
        drone_output = Dense(num_classes_drone, activation='softmax', name='drone_output')(x)

        # Modelo multitarefa
        model = Model(inputs=inputs, outputs=[status_output, direction_output, drone_output])

        return model

    # Criar o modelo com a nova saída de identificação do drone
    model = build_deep_1dcnn_resnet(input_shape, num_classes_status, num_classes_direction, num_classes_drone, num_blocks)

    # Compilar o modelo com a nova saída
    model.compile(optimizer=Adam(learning_rate=0.0001),
                  loss={'status_output': 'categorical_crossentropy',
                        'direction_output': 'categorical_crossentropy',
                        'drone_output': 'categorical_crossentropy'},
                  metrics={'status_output': 'accuracy',
                           'direction_output': 'accuracy',
                           'drone_output': 'accuracy'})

    return model

