import os
import numpy as np
import librosa
from scipy.fftpack import dct
from gammatone.gtgram import gtgram

# Função para extrair MFCC
def extract_mfcc(signal, sr, n_mfcc=13, n_fft=2048, hop_length=512, n_mels=16):
    stft = np.abs(librosa.stft(signal, n_fft=n_fft, hop_length=hop_length))
    power_spectrum = np.square(stft)
    mel_filter_bank = librosa.filters.mel(sr=sr, n_fft=n_fft, n_mels=n_mels)
    mel_spectrum = np.dot(mel_filter_bank, power_spectrum)
    log_mel_spectrum = np.log(mel_spectrum + 1e-9)
    mfcc = dct(log_mel_spectrum, type=2, axis=0, norm='ortho')[:n_mfcc]
    return mfcc

# Função para extrair GTCC
def extract_gtcc(signal, sr, n_gtcc=13, n_fft=2048, hop_length=512, n_gammas=16, f_min=50):
    window_size = n_fft / sr
    hop_size = hop_length / sr
    gamma_spectrum = gtgram(signal, sr, window_size, hop_size, n_gammas, f_min)
    log_gamma_spectrum = np.log(gamma_spectrum + 1e-9)
    gtcc = dct(log_gamma_spectrum, type=2, axis=0, norm='ortho')[:n_gtcc]
    return gtcc

# Função para extrair MFCC e GTCC
def extract_mfcc_gtcc(signals, sr):
    mfcc_list = []
    gtcc_list = []
    for signal in signals:
        mfcc = extract_mfcc(signal, sr)
        gtcc = extract_gtcc(signal, sr)
        mfcc_list.append(mfcc)
        gtcc_list.append(gtcc)
    return np.array(mfcc_list), np.array(gtcc_list)

# Função para carregar todos os arquivos de áudio de um diretório
def load_all_audio_from_directory(directory):
    audio_signals = []
    sample_rate = None
    for filename in os.listdir(directory):
        if filename.endswith('.wav'):
            filepath = os.path.join(directory, filename)
            signal, sr = librosa.load(filepath, sr=None)
            audio_signals.append(signal)
            if sample_rate is None:
                sample_rate = sr
    return np.array(audio_signals), sample_rate

# Função para extrair as características MFCC e GTCC para os conjuntos de dados
def extract_features_for_merged_dataset(base_dir='/content/Merged'):
    sets = ['train', 'valid', 'test']
    features = {}

    for data_set in sets:
        mic1_dir = os.path.join(base_dir, data_set, 'mic1')
        mic2_dir = os.path.join(base_dir, data_set, 'mic2')

        # Carregar sinais de áudio para Mic1 e Mic2
        X_mic1, sr_mic1 = load_all_audio_from_directory(mic1_dir)
        X_mic2, sr_mic2 = load_all_audio_from_directory(mic2_dir)

        # Extrair características MFCC e GTCC
        mfcc_mic1, gtcc_mic1 = extract_mfcc_gtcc(X_mic1, sr_mic1)
        mfcc_mic2, gtcc_mic2 = extract_mfcc_gtcc(X_mic2, sr_mic2)

        # Armazenar os resultados
        features[f'mfcc_{data_set}'] = (mfcc_mic1, mfcc_mic2)
        features[f'gtcc_{data_set}'] = (gtcc_mic1, gtcc_mic2)

    return features

# Extrair características para o conjunto de dados Merged
