# Função para extrair MFCC
def extrair_mfcc(sinal, sr, n_mfcc=13, n_fft=2048, hop_length=512, n_mels=16):
    stft = np.abs(librosa.stft(sinal, n_fft=n_fft, hop_length=hop_length))
    power_spectrum = np.square(stft)
    mel_filter_bank = librosa.filters.mel(sr=sr, n_fft=n_fft, n_mels=n_mels)
    mel_spectrum = np.dot(mel_filter_bank, power_spectrum)
    log_mel_spectrum = np.log(mel_spectrum + 1e-9)
    mfcc = dct(log_mel_spectrum, type=2, axis=0, norm='ortho')[:n_mfcc]
    return mfcc

# Função para extrair GTCC
def extrair_gtcc(sinal, sr, n_gtcc=13, n_fft=2048, hop_length=512, n_gammas=16, f_min=50):
    window_size = n_fft / sr
    hop_size = hop_length / sr
    gamma_spectrum = gtgram(sinal, sr, window_size, hop_size, n_gammas, f_min)
    log_gamma_spectrum = np.log(gamma_spectrum + 1e-9)
    gtcc = dct(log_gamma_spectrum, type=2, axis=0, norm='ortho')[:n_gtcc]
    return gtcc

# Função para gerar e salvar espectrogramas
def gerar_e_salvar_espectrograma(sinal, sr, filepath, tipo='mfcc'):
    if tipo == 'mfcc':
        features = extrair_mfcc(sinal, sr)
    elif tipo == 'gtcc':
        features = extrair_gtcc(sinal, sr)
    else:
        raise ValueError("Tipo de espectrograma desconhecido. Use 'mfcc' ou 'gtcc'.")

    plt.figure(figsize=(10, 4))
    plt.imshow(features, aspect='auto', origin='lower')
    plt.title(f"Espectrograma {tipo.upper()}")
    plt.colorbar(format='%+2.0f dB')
    plt.tight_layout()

    # Salvar o espectrograma como imagem
    save_path = filepath.replace('.wav', f'_{tipo}.png')
    plt.savefig(save_path)
    plt.close()

# Processar cada arquivo de áudio no diretório
for arquivo in os.listdir(output_dir):
    if arquivo.endswith('.wav'):
        caminho_arquivo = os.path.join(output_dir, arquivo)
        sinal, sr = librosa.load(caminho_arquivo, sr=None)

        # Gerar e salvar espectrograma MFCC
        gerar_e_salvar_espectrograma(sinal, sr, caminho_arquivo, tipo='mfcc')

        # Gerar e salvar espectrograma GTCC
        gerar_e_salvar_espectrograma(sinal, sr, caminho_arquivo, tipo='gtcc')

