import os
import numpy as np
import matplotlib.pyplot as plt

# Função para extrair SNR do nome do arquivo
def extract_snr_from_filename(filename):
    snr_value = float(filename.split('_')[-1].split('=')[-1].replace('.wav', ''))
    return snr_value

# Função para correlacionar accuracy com SNR
def analyze_performance_by_snr(X_test, y_true, y_pred, filenames):
    snr_values = np.array([extract_snr_from_filename(f) for f in filenames])
    snr_ranges = np.arange(10, 17)  # Faixas de SNR de 10 a 15 dB
    accuracies = []

