# Parte 2: Funções para Augmentação de Dados e Extração de Features

def carregar_e_augmentar_audio(caminho_arquivo, sr=22050, fator_pitch=2, fator_ruido=0.05):
    # Carregar o arquivo de áudio
    sinal, sr = librosa.load(caminho_arquivo, sr=sr)

    # Alteração de pitch
    sinal_pitch_shifted = librosa.effects.pitch_shift(sinal, sr=sr, n_steps=fator_pitch)

    # Adição de ruído branco
    ruido = np.random.randn(len(sinal))
    sinal_ruidoso = sinal + fator_ruido * ruido

    return sinal_pitch_shifted, sinal_ruidoso, sr

def extrair_mfcc(sinal, sr, n_mfcc=13, n_fft=2048, hop_length=512, n_mels=16):
    stft = np.abs(librosa.stft(sinal, n_fft=n_fft, hop_length=hop_length))
    power_spectrum = np.square(stft)
    mel_filter_bank = librosa.filters.mel(sr=sr, n_fft=n_fft, n_mels=n_mels)
    mel_spectrum = np.dot(mel_filter_bank, power_spectrum)
    log_mel_spectrum = np.log(mel_spectrum + 1e-9)
    mfcc = dct(log_mel_spectrum, type=2, axis=0, norm='ortho')[:n_mfcc]
    return mfcc

def extrair_gtcc(sinal, sr, n_gtcc=13, n_fft=2048, hop_length=512, n_gammas=16, f_min=50):
    window_size = n_fft / sr
    hop_size = hop_length / sr

    if hop_size <= 0:
        hop_size = 1 / sr

    gamma_spectrum = gtgram(sinal, sr, window_size, hop_size, n_gammas, f_min)
    log_gamma_spectrum = np.log(gamma_spectrum + 1e-9)
    gtcc = dct(log_gamma_spectrum, type=2, axis=0, norm='ortho')[:n_gtcc]

    return gtcc

def extrair_features_com_augmentacao(caminho_diretorio):
    lista_mfcc = []
    lista_gtcc = []
    rótulos_status = []
    rótulos_direcao = []

    for filename in os.listdir(caminho_diretorio):
        if filename.endswith('.wav'):
            caminho_arquivo = os.path.join(caminho_diretorio, filename)
            sinal_pitch_shifted, sinal_ruidoso, sr = carregar_e_augmentar_audio(caminho_arquivo)

            # Extrair features para os três sinais: original, pitch_shifted e ruidoso
            mfcc_original = extrair_mfcc(sinal_pitch_shifted, sr)
            gtcc_original = extrair_gtcc(sinal_pitch_shifted, sr)
            mfcc_pitch_shifted = extrair_mfcc(sinal_pitch_shifted, sr)
            gtcc_pitch_shifted = extrair_gtcc(sinal_pitch_shifted, sr)
            mfcc_ruidoso = extrair_mfcc(sinal_ruidoso, sr)
            gtcc_ruidoso = extrair_gtcc(sinal_ruidoso, sr)

            # Combinar as features
            lista_mfcc.extend([mfcc_original, mfcc_pitch_shifted, mfcc_ruidoso])
            lista_gtcc.extend([gtcc_original, gtcc_pitch_shifted, gtcc_ruidoso])

            # Extrair rótulos do nome do arquivo
            direction_label, status_label = extract_labels_from_filename(filename)

            # Replicar os rótulos para corresponder ao número de versões aumentadas
            rótulos_status.extend([status_label] * 3)
            rótulos_direcao.extend([direction_label] * 3)

    return np.array(lista_mfcc), np.array(lista_gtcc), np.array(rótulos_status), np.array(rótulos_direcao)

# Parte 3: Normalização das Features e Combinação

def normalizar_features(features, scaler=None):
    if scaler is None:
        scaler = StandardScaler()
        scaled_features = scaler.fit_transform(features.reshape(-1, features.shape[-1])).reshape(features.shape)
    else:
        scaled_features = scaler.transform(features.reshape(-1, features.shape[-1])).reshape(features.shape)
    return scaled_features, scaler

# Carregar e Extrair Features com Augmentação
mfcc_train_A, gtcc_train_A, y_train_status_A, y_train_direction_A = extrair_features_com_augmentacao('/content/A/train/mic1')
mfcc_val_A, gtcc_val_A, y_val_status_A, y_val_direction_A = extrair_features_com_augmentacao('/content/A/valid/mic1')
mfcc_test_A, gtcc_test_A, y_test_status_A, y_test_direction_A = extrair_features_com_augmentacao('/content/A/test/mic1')

# Normalizar as Features
