mfcc_val_A, _ = normalizar_features(mfcc_val_A, scaler_mfcc_A)
mfcc_test_A, _ = normalizar_features(mfcc_test_A, scaler_mfcc_A)

gtcc_train_A, scaler_gtcc_A = normalizar_features(gtcc_train_A)
gtcc_val_A, _ = normalizar_features(gtcc_val_A, scaler_gtcc_A)
gtcc_test_A, _ = normalizar_features(gtcc_test_A, scaler_gtcc_A)

# Combinar Features MFCC e GTCC
X_train_combined_A = np.concatenate((mfcc_train_A, gtcc_train_A), axis=-1)
