# Parte 4: Definição do Modelo e Treinamento

def bloco_residual(x, filtros, kernel_size=3):
    res = x
    out = Conv1D(filtros, kernel_size, padding='same')(x)
    out = BatchNormalization()(out)
    out = Activation('relu')(out)

    out = Conv1D(filtros, kernel_size, padding='same')(out)
    out = BatchNormalization()(out)

    out = Add()([res, out])
    out = Activation('relu')(out)
    return out

def construir_1dcnn_resnet_profundo(input_shape, num_classes_status, num_classes_direction, num_blocks=8):
    inputs = Input(shape=input_shape)

    # Primeira camada Conv1D
    x = Conv1D(128, kernel_size=3, padding='same')(inputs)
    x = BatchNormalization()(x)
    x = Activation('relu')(x)

    # Blocos residuais
    for _ in range(num_blocks):
        x = bloco_residual(x, 128)

    x = Flatten()(x)

    # Adicionando uma camada densa antes das saídas
    x = Dense(256, activation='relu')(x)
    x = BatchNormalization()(x)
    x = Dropout(0.5)(x)

    # Cabeça de classificação de status (tarefa principal)
    status_output = Dense(num_classes_status, activation='softmax', name='status_output')(x)

    # Cabeça de classificação de direção (tarefa auxiliar)
    direction_output = Dense(num_classes_direction, activation='softmax', name='direction_output')(x)

    # Modelo multitarefa
    model = Model(inputs=inputs, outputs=[status_output, direction_output])

    return model

# Parâmetros do Modelo
input_shape = (X_train_combined_A.shape[1], X_train_combined_A.shape[2])  # Tamanho da entrada (tempo x canais)
num_classes_status = y_train_status_A.shape[1]  # Número de classes de status
num_classes_direction = y_train_direction_A.shape[1]  # Número de classes de direção

# Construir e Compilar o Modelo
model = construir_1dcnn_resnet_profundo(input_shape, num_classes_status, num_classes_direction, num_blocks=8)
model.compile(optimizer=Adam(learning_rate=0.0001),
              loss={'status_output': 'categorical_crossentropy', 'direction_output': 'categorical_crossentropy'},
              metrics={'status_output': 'accuracy', 'direction_output': 'accuracy'})

# Treinar o Modelo
history_A = model.fit(X_train_combined_A, [y_train_status_A, y_train_direction_A],
                      validation_data=(X_val_combined_A, [y_val_status_A, y_val_direction_A]),
                      epochs=100,
                      batch_size=32)

# Parte 5: Avaliação do Modelo

# Avaliar o Modelo
results_A = model.evaluate(X_test_combined_A, {'status_output': y_test_status_A, 'direction_output': y_test_direction_A})
print("Drone A - Test loss, Test accuracy:", results_A)

