    fault_map = {'N': 0, 'MF1': 1, 'MF2': 2, 'MF3': 3, 'MF4': 4,
                 'PC1': 5, 'PC2': 6, 'PC3': 7, 'PC4': 8}
    status_label = fault_map[fault]

    return direction_label, status_label

