    x = BatchNormalization()(x)
    x = Activation('relu')(x)

    # Blocos residuais
    for _ in range(hp.Int('num_blocks', 4, 12)):
        x = residual_block(x, filters)

    x = Flatten()(x)

    # Adicionando uma camada densa antes das saídas
    units = hp.Int('units', min_value=128, max_value=512, step=128)
    x = Dense(units, activation='relu')(x)
    x = BatchNormalization()(x)
    dropout_rate = hp.Float('dropout', 0.1, 0.5, step=0.1)
    x = Dropout(dropout_rate)(x)

    # Cabeça de classificação de status (tarefa principal)
