import os
import librosa
import numpy as np
from scipy.fftpack import dct
import tensorflow as tf
from tensorflow.keras.layers import Input, Conv1D, BatchNormalization, Activation, Add, Flatten, Dense, Dropout
from tensorflow.keras.models import Model
from tensorflow.keras.optimizers import Adam
from sklearn.preprocessing import StandardScaler
from gammatone.gtgram import gtgram

def load_all_audio_from_directory(directory_path):
    audio_data_list = []
    sr_list = []
    for filename in os.listdir(directory_path):
        if filename.endswith('.wav'):
            file_path = os.path.join(directory_path, filename)
            audio_data, sr = librosa.load(file_path, sr=None)
            audio_data_list.append(audio_data)
            sr_list.append(sr)
    return np.array(audio_data_list), sr_list[0]

def extract_labels_from_filename(filename):
    parts = filename.split('_')
    maneuvering_direction = parts[1]
    fault = parts[2]
    direction_map = {'F': 0, 'B': 1, 'R': 2, 'L': 3, 'C': 4, 'CC': 5}
    fault_map = {'N': 0, 'MF1': 1, 'MF2': 2, 'MF3': 3, 'MF4': 4, 'PC1': 5, 'PC2': 6, 'PC3': 7, 'PC4': 8}
    return direction_map[maneuvering_direction], fault_map[fault]

def generate_labels_for_dataset(directory_path):
    direction_labels = []
    status_labels = []
    for filename in os.listdir(directory_path):
        if filename.endswith('.wav'):
            direction_label, status_label = extract_labels_from_filename(filename)
            direction_labels.append(direction_label)
            status_labels.append(status_label)
    return np.array(direction_labels), np.array(status_labels)

